$(document).ready(function() {
  // Get the image element.
  var image = $("#image");

  // When the image is loaded, remove the background.
  image.on("load", function() {
      // Get the image's src attribute.
      var src = image.attr("src");

      // Create a new image element with the same src attribute.
      var newImage = $("<img>");
      newImage.attr("src", src);

      // Remove the background from the new image.
      newImage.css("background-image", "none");

      // Replace the old image with the new image.
      image.replaceWith(newImage);
  });
});
